#!/usr/bin/env python

'''
This script will XOR the data. 
The key and the payload must be in hex format
'''

import binascii

enc = "2f21077b4c3e52242c3850450904680377761c73711a0470756d0b04061f77007b6b00716f000f6d70700b1b05037f1c76711c7774033b4b242855505a502b577f6a5732221b4e393a39434d181e3e4a3a4838342c525f2d277c16415941691c31365a77731819352f31161b47422e007145"
key = "42423246413336414141393534314630"
#enc = ""
#key = "38"
enc_length = len(enc) / 2
key_length = len(key)

a = 0
b = 2
c = 0
d = 2
dec = ""

## If the key is single byte
if (key_length == 2):
	for cnt in range(enc_length):
		dec += hex(int(enc[a:b], 16) ^ int(key[c:d], 16)).replace('0x', '').zfill(2)
		a += 2
		b += 2
else:
	for cnt in range(enc_length):
		## when the key gets to end, reset the key to beginning
		if (d == key_length):
			dec += hex(int(enc[a:b], 16) ^ int(key[c:d], 16)).replace('0x', '').zfill(2)
			## I put zifll because I didn't want to lose the leading zero in hex
			## eg) 0xd -> 0x0d (this is what I want)
			c = 0 # reset the key to beginning
			d = 2 # reset the key to beginning
			a += 2
			b += 2
		else:
			dec += hex(int(enc[a:b], 16) ^ int(key[c:d], 16)).replace('0x', '').zfill(2)
			a += 2
			b += 2
			c += 2
			d += 2

print("raw hex: ")
print("#######")
print(dec)

print("")
print("decoded string: ")
print("#######")
print(binascii.unhexlify(dec))
